public class largest
{
	public void Test(int x,int y,int z)
		{
			int max=0;
			max=x;
			if(y>max)
			max=y;
			if(z>max)
			max=z;
			System.out.println("Largest number is " + max);
		}
	public static void main(String args[])
		{
			largest ob=new largest();
			ob.Test(4, 5, 6);
		}
}

