				ob.compute(2,3);
			}
	}

public class case_char
	{
		public void Test(char ch)
			{
				if(ch>=48 && ch<=57)
					System.out.println("is a digit");
				else if(ch>=65 && ch<=90)
					System.out.println("is a UPPERCASE character");
				else if(ch>97 && ch<=122)
					System.out.println("is a lowercase character");
				else
					System.out.println("is a Special character");
			}
		public static void main(String args[])
			{
				case_char ob=new case_char();
				System.out.println("passing d");
				ob.Test('d');
				System.out.println("passing A");
				ob.Test('A');
				System.out.println("passing 1");
				ob.Test('1');
				System.out.println("passing *");
				ob.Test('*');
			}
	}

public class area
	{
		public void choice(int a,int b,int s)
			{
				switch(s)
					{
						case 1:System.out.println("sum is"+ (a+b));
						break;
						case 2:System.out.println("difference is"+ (a-b));
						break;
						case 3:System.out.println("product is"+ (a*b));
						break;
						case 4:System.out.println("division is"+ (a/b));
						break;
						default:System.out.println("wrong choice");
					}
			}
		public static void main(String args[])
			{
				area ob=new area ();
				ob.choice(4,1,1);
				ob.choice(4,1,2);
				ob.choice(4,1,3);
				ob.choice(4,1,4);
			}
	}

import java.util.Scanner;
public class BubbleSort
	{
		public static void main(String args[])
			{
				int n,swap;
				Scanner s=new Scanner(System.in);
				System.out.println("enter number of integers to sort");
				n=s.nextInt();
				int[] arr=new int[n];
				System.out.println("enter" +n+ "integers");
				for(int i=0;i < n;i++)
					arr[i]=s.nextInt();
				for(int i=0;i < n-1;i++)
					{
						for(int j=0;j < n-i-1;j++)
							{
								if(arr[j] > arr[j+1])
									{
										swap=arr[j];
										arr[j]=arr[j+1];
										arr[j+1]=swap;
									}
							}
					}
				System.out.println("sorted numbers are");
				for(int i=0;i < n;i++)
					System.out.println(arr[i]);
			}
	}

