import java.util.Scanner;
public class Selection
	{
		public static void selsort(int a[],int size)
			{
				int i,j,k,small,tmp,pos;
				for(i=0;i<size;i++)
					{
						small=a[i];
						pos=i;
						for(j=i+1;j<size;j++)
							{
								if(a[j]<small)
									{
										small=a[j];
										pos=j;
									}
							}
						tmp=a[i];
						a[i]=a[pos];
						a[pos]=tmp;
						System.out.println("
array after pass "+(i+1)+" is");
						for(k=0;k<size;k++)
							System.out.print(a[k]+"	");
					}
				System.out.println("
array in sorted form is");
				for(i=0;i <size;i++)
					System.out.print(a[i]+"	");
			}
		public static void main(String args[])
			{
				int arrsize,c;
				Scanner s=new Scanner(System.in);
				System.out.println("enter number of elements to sort.");
				arrsize=s.nextInt();
				int arr[]=new int[arrsize];
				System.out.println("enter "+(arrsize)+" numbers");
				for(c=0;c <arrsize;c++)
					arr[c]=s.nextInt();
				selsort(arr,arrsize);
			}
	}

import java.util.Scanner;
public class binary
	{
		public static void search(int a[],int size,int n)
			{
				int f=0,L,U,M=0;
				L=0;
				U=size-1;
				while(L<=U)
					{
						M=(L+U)/2;
						if(n<a[M])
							L=M+1;
						else if(n<a[M])
							U=M-1;
						else
							{
								f=1;
								break;
							}
					}
				if(f==1)
					System.out.println("number is present at position "+(M+1));
					else
						System.out.println("number is not present");
			}
			public static void main(String args[])
				{
					int arr[],arrsize,i,x;
					Scanner s=new Scanner(System.in);
					System.out.println("enter number of elements");
					arrsize=s.nextInt();
					arr=new int[arrsize];
					System.out.println("enter number in ascending order");
					for(i=0;i<arrsize;i++)
						arr[i]=s.nextInt();
					System.out.println("enter number to be searched");
					x=s.nextInt();
					search(arr,arrsize,x);
				}
	}

public class ASCII
	{
		public static char ASCIItochar(int asciivalue)
			{
				return (char)asciivalue;
			}
