public class merge 
	{
		public void concat(int x[],int y[],int xlength,int ylength)
			{
				int i;
				int mergearr[]=new int[xlength+ylength];
				for(i=0;i<(xlength+ylength);i++)
					{
						if(i<xlength) 
							mergearr[i]=x[i];
						else
							mergearr[i]=y[i-xlength];
					}
				System.out.print("merged array is ");
				for(i=0;i<(xlength+ylength);i++)
					System.out.print(mergearr[i]+" ");
			}
		public static void main(String args[])
			{
				int j;
				int arr1[]={2,3,6,8};
				int arr2[]={1,4,9,5,12};
				System.out.print("1st array is ");
				for(j=0;j<4;j++)
					System.out.print(arr1[j]+" ");
				System.out.println();
				System.out.print("2nd array is ");
				for(j=0;j<5;j++)
					System.out.print(arr2[j]+" ");
				System.out.println();
				merge ob=new merge();
				ob.concat(arr1, arr2, 4, 5);
			}
	}

import java.io.*;public class delarr
		public void del(int a[],int pos,int length)
		{
			for(int i=pos-1;i<length-1;i++)
				a[i]=a[i+1];
			a[length-1]=0;
			System.out.print("new array is ");
			for(int i=0;i<length-1;i++)
			System.out.print(a[i]+" ");
		}
	public static void main(String args[]) throws IOException
		{
			int lenarr,n;
			int arr[];
			BufferedReader br=new BufferedReader(new InputStreamReader(System.in));
			System.out.println("enter length of array");
			String s1=br.readLine();
			lenarr=Integer.parseInt(s1);
			arr=new int[lenarr];
			System.out.println("enter values in array");
			for(int i=0;i<lenarr;i++)
				{
					String s2=br.readLine();
					arr[i]=Integer.parseInt(s2);
				}
			System.out.println("enter position of number to be deleted");
			String s3=br.readLine();
			n=Integer.parseInt(s3);
			delarr ob=new delarr();
			ob.del(arr,n,lenarr);
		}
}

"import java.util.Scanner;public class addmatrix
{
	int r,c;
	int matrix1[][];
	int matrix2[][];
	int result[][];
	public void input()
		{
			Scanner s=new Scanner(System.in);
			System.out.println("enter number of rows and then column");
			r=s.nextInt();
			c=s.nextInt();
			matrix1=new int[r][c];
			matrix2=new int[r][c];
			System.out.println("enter numbers in 1st matrix");
			for(int i=0;i<r;i++)
				{
					for(int j=0;j<c;j++)
					matrix1[i][j]=s.nextInt();
				}
			System.out.println("enter numbers in 2nd matrix");
			for(int i=0;i<r;i++)
				{
					for(int j=0;j<c;j++)
					matrix2[i][j]=s.nextInt();
				}
			s.close();
		}
	public void add()
		{
			result=new int[r][c];
			for(int i=0;i<r;i++)
				for(int j=0;j<c;j++)
					result[i][j]=matrix1[i][j] + matrix2[i][j];
		}
	public void display()
		{
			System.out.println("new matrix is");
			for(int i=0;i<r;i++)
				{
					for(int j=0;j<c;j++)
						{
							System.out.print(result[i][j]+" ");
						}
					System.out.println();
				}
		}
	public static void main(String args[])
		{
			addmatrix ob=new addmatrix();
			ob.input();
			ob.add();
			ob.display();
		}
}

