import java.util.Scanner;public class multiplymatrix
{
	int r1,c1,r2,c2;
	int matrix1[][];
	int matrix2[][];
	int result[][];
	public void input()
		{
			Scanner s=new Scanner(System.in);
			System.out.println("enter number of rows and then column of 1st matrix");
			r1=s.nextInt();
			c1=s.nextInt();
			matrix1=new int[r1][c1];
			System.out.println(enter numbers in 1st matrix);
			for(int i=0;i<r1;i++)
				{
					for(int j=0;j<c1;j++)
					matrix1[i][j]=s.nextInt();
				}
			System.out.println("enter number of rows and then column of 2nd matrix");
			r2=s.nextInt();
			c2=s.nextInt();
			if(c1!=r2)
				{
					s.close();
					return;
				}
			matrix2=new int[r2][c2];
			System.out.println("enter numbers in 2nd matrix");
			for(int i=0;i<r2;i++)
				{
					for(int j=0;j<c2;j++)
						matrix2[i][j]=s.nextInt();
				}
			s.close();
		}
	public void multiply_matrix()
		{
			result=new int[r1][c2];
			if(c1!=r2)
				return;
			for(int i=0;i<r1;i++)
				{
					for(int j=0;j<c2;j++)
						{
							result[i][j]=0;
							for(int k=0;k<c1;k++)								result[i][j]=result[i][j]+(matrix1[i][k]*matrix2[k][j]);
						}
				}
		}
	public void display()
		{
			if(c1!=r2)
				{
					System.out.println("entered matrix are not compatible for multiplication");
					return;
				}
			System.out.println("resultent matrix is");
			for(int i=0;i<r1;i++)
				{
					for(int j=0;j<c2;j++)
						{
							System.out.print(result[i][j]+" ");
						}
					System.out.println();
				}
		}
	public static void main(String args[])
		{
			multiplymatrix ob=new multiplymatrix();
			ob.input();
			ob.multiply_matrix();
			ob.display();
		}
}

import java.util.Scanner;
public class transpose
	{
	int r,c;
	int orignal[][];
	int trans[][];
	public void input()
		{
			Scanner s=new Scanner(System.in);
			System.out.println("nter number of rows and then column");
			r=s.nextInt();
			c=s.nextInt();
			orignal=new int[r][c];
			System.out.println("enter numbers in matrix");
			for(int i=0;i<r;i++)
				for(int j=0;j<c;j++)
					orignal[i][j]=s.nextInt();
			s.close();			}
	public void transmatrix()
		{
			trans=new int[c][r];
			for(int i=0;i<c;i++)
				for(int j=0;j<r;j++)
					trans[i][j]=orignal[j][i];			}
	public void display()
		{
			System.out.println("entered matrix is");
			for(int i=0;i<r;i++)
				{
					for(int j=0;j<c;j++)
					{
							System.out.print(orignal[i][j]+" ");
						}
					System.out.println();
				}
			System.out.println("transposed matrix is");
			for(int i=0;i<c;i++)
				{
					for(int j=0;j<r;j++)
						{
							System.out.print(trans[i][j]+" ");
						}
					System.out.println();
				}
		}
	public static void main(String args[])
		{
			transpose ob=new transpose();
			ob.input();
			ob.transmatrix();
			ob.display();
		}
}
